#!/bin/sh# $1=directory_name; $2=Area1; $3=Food1area1=$2area2=$(echo "50 - $2" | bc)food1=$3food2=$(echo "100 - $3" | bc)echo "Outputting to ${1}.eps..."echo "Area ${area1} / ${area2}     Food: ${food1}% / ${food2}%"# /sw/bin/gnuplot << EOFgnuplot << EOFset terminal postscript eps monochrome enhanced "Helvetica" 25# set terminal postscript eps solid monochrome enhanced "Helvetica" 18set output "$1.eps"set title "Area: ${area1} / ${area2}       Food Fraction: ${food1}% / ${food2}%" font "Helvetica,26"set xlabel "Timestep"set ylabel "% of Population"set gridset palette grayset key offset xrange [0:10000]set xtics 2000set yrange [0:100]set ytics 10foodband1(x)=${food1}foodband2(x)=${food2}plot '$1/FB0.plt' using 1:8 w l lw 2 lt -1, '$1/FB1.plt' using 1:8 w lines lw 2 lt 1, foodband1(x) lt 0 lw 10 notitle, foodband2(x) lt 0 lw 10 notitleEOF